# -*- coding:utf-8 -*-# Date: 2018-03-07 16:22:37# Author: dekivenimport os__version__ = '0.2.1'def _untar(fname, dirs) :    import tarfile    try:        print('unzip %s  ->   %s'%(fname, dirs))        t = tarfile.open(fname)        if not os.path.isdir(dirs) :            os.makedirs(dirs)        t.extractall(path = dirs)        return True    except Exception as e:        print(e)        return Falsedef installDND() :    from DKVTools.Funcs import isWindows    from DKVTools.Funcs import isWindows    import struct    d = os.path.dirname(__file__)    if isWindows() :        if struct.calcsize("P") == 4 :            _untar(os.path.join(d, 'res/tkdnd-2.9.2-windows-x86.tar.gz'), os.path.join(d, '../../../tcl'))        else :            _untar(os.path.join(d, 'res/tkdnd-2.9.2-windows-x64.tar.gz'), os.path.join(d, '../../../tcl'))    elif isMac() :        _untar(os.path.join(d, 'res/tkdnd-2.9.2-osx-x64.tgz'), '/Library/Tcl')    elif isLinux() :        _untar(os.path.join(d, 'res/tkdnd-2.9.2-linux-x64.tgz'), '/Library/Tcl')    _untar(os.path.join(d, 'res/TkinterDnD2-0.3.tar.gz'), os.path.join(d, '../'))try :    from TkinterDnD2 import TkinterDnDexcept Exception as e :    # raise e    installDND()