import blurry.core as bl
import streamlit as st
import numpy as np
from PIL import Image

# Cell
st.title("Hello");

# Cell
st.write("Hide faces")
st.write(bl.testing())
uploaded_file = st.file_uploader("Upload an image", type=['png', 'jpg', 'jpeg'], accept_multiple_files=False)

# Cell
if uploaded_file is not None:
    st.write("File uploaded!!!!!")
    img = Image.open(uploaded_file)
    img_array = np.array(img)
    anon_img = bl.anonymize(img_array, mode='pixelate')
    st.image(anon_img, caption="The caption", use_column_width=True)
st.write("Does this get printed?")